$(document).ready(function () {
  $('#burguerIcon').click(function() {
    $(this).toggleClass("is-active"),
    $('.main-header').toggleClass("is-active"),
    $('.mobileNav').toggleClass("is-active")
  });
}); 

$(document).ready(function () {
  $('#client-slider').slide({
    slideShow : true,
    speed : 3000
  });
});

$(document).ready(function () {
  $(window).scroll(function () {
    var $header = $('.main-header')
    var $headerTop = $(window).scrollTop()

    if ($headerTop > 1) {
      $header.addClass('is-scrolled')
    } else {
      $header.removeClass('is-scrolled')
    }
  });
});
function sendEmail(){
  message = "Name: " + $("#name").val() + "Email: " + $("#mail").val() + "Message: " + $("#message").val();

  $.post("https://formspree.io/federico@disruptiveangels.com", {message: message})
  .done(function() {
    $( ".contact" ).empty();
    $( ".contact" ).append( '<h2 class="title">Your message was send succesfully.</h2>' );
    setTimeout(function() {
      $('.featherlight').click();
    }, 1000);
  })
  .fail(function() {
    $( ".form-message" ).append( 'UPS! Try again.' );
  });
  return false;
}
